package expression.exceptions;import expression.*;public class CheckedAdd extends CheckedOperation {    public CheckedAdd(All first, All second) {        super.first = first;        super.second = second;        super.sign = " + ";    }    public int operation(int first, int second) {        if (first + second <= 0 && first > 0 && second > 0 || first + second >= 0 && first < 0 && second < 0) {            throw new OverflowException("overflow");        }        return first + second;    }}