package expression.exceptions;import expression.*;public class CheckedDivide extends CheckedOperation {    public CheckedDivide(All first, All second) {        super.first = first;        super.second = second;        super.sign = " / ";    }    public int operation(int first, int second) {        if (second == 0) {            throw new ArithmeticException("division by zero");        }        if (first == Integer.MIN_VALUE && second == -1) {            throw new OverflowException("overflow");        }        return first / second;    }}