package expression.exceptions;import expression.*;public class CheckedL0 extends UnaryOperations {    public CheckedL0(All expression, boolean hasBrackets) {        super.expression = expression;        super.hasBrackets = hasBrackets;        super.name = "l0 ";        if (hasBrackets) {            super.name = "l0";        }    }    public int operation(int val) {        int res = 0, cur = 0;        String binary = String.format("%32s", Integer.toBinaryString(val)).replace(' ', '0');        while (cur < binary.length() && binary.charAt(cur++) == '0') {            res++;        }        return res;    }}