package expression.exceptions;import expression.*;public class CheckedMultiply extends CheckedOperation {    public CheckedMultiply(All first, All second) {        super.first = first;        super.second = second;        super.sign = " * ";    }    public int operation(int first, int second) {        if (first != 0 && second != 0 && ((first * second) / first != second || (first * second) / second != first)) {            throw new OverflowException("overflow");        }        return first * second;    }}