package expression.exceptions;import expression.*;public class CheckedNegate extends UnaryOperations {    public CheckedNegate(All expression) {        super.expression = expression;        super.hasBrackets = false;        super.name = "-";    }    public CheckedNegate(All expression, boolean hasBrackets) {        super.expression = expression;        super.hasBrackets = hasBrackets;        super.name = "-";    }    public int operation(int val) {        if (val == Integer.MIN_VALUE) {            throw new OverflowException("overflow");        }        return -val;    }}