package expression.exceptions;import expression.*;public class CheckedSubtract extends CheckedOperation {    public CheckedSubtract(All first, All second) {        super.first = first;        super.second = second;        super.sign = " - ";    }    public int operation(int first, int second) {        if (first - second <= 0 && first > second || first - second >= 0 && first < second) {            throw new OverflowException("overflow");        }        return first - second;    }}