package expression.exceptions;import expression.*;public class CheckedT0 extends UnaryOperations {    public CheckedT0(All expression, boolean hasBrackets) {        super.expression = expression;        super.hasBrackets = hasBrackets;        super.name = "t0 ";        if (hasBrackets) {            super.name = "t0";        }    }    public int operation(int val) {        String binary = String.format("%32s", Integer.toBinaryString(val)).replace(' ', '0');        int res = 0, cur = 31;        while (cur >= 0 && binary.charAt(cur--) == '0') {            res++;        }        return res;    }}