package expression.generic;public class GenericUnaryMinus<T> implements GenericGeneralExpression<T> {    protected GenericGeneralExpression<T> expression;    protected boolean hasBrackets;    protected NumberMode<T> calculator;    public GenericUnaryMinus(GenericGeneralExpression<T> expression, boolean hasBrackets, NumberMode<T> calculator) {        this.expression = expression;        this.hasBrackets = hasBrackets;        this.calculator = calculator;    }    @Override    public T evaluate(T val) {        return calculator.negate(expression.evaluate(val));    }    @Override    public T evaluate(T val1, T val2, T val3) {        return calculator.negate(expression.evaluate(val1, val2, val3));    }    @Override    public String toString() {        if (hasBrackets || (expression instanceof GenericConst && calculator.compare(expression.evaluate(calculator.parse("0")), calculator.parse("0")))) {            return "-(" + expression.toString() + ")";        }        return "-" + expression.toString();    }    @Override    public boolean equals(Object object) {        if (object == null || this.getClass() != object.getClass()) {            return false;        }        GenericUnaryMinus var2 = (GenericUnaryMinus) object;        return expression.equals(var2.expression);    }    @Override    public int hashCode() {        return 17 * expression.hashCode();    }}