package expression.generic;import java.util.Map;import java.util.Objects;public class Main {    private final static Map<String, String> Options = Map.of(            "-i", "i",            "-d", "d",            "-bi", "bi",            "-u", "u",            "-s", "s",            "-l", "l"    );    public static void main(String[] args) {        GenericTabulator tabulator = new GenericTabulator();        Object[][][] table = new Object[5][5][5];        try {            table = tabulator.tabulate(Options.get(args[0]), args[1], -2, 2, -2, 2, -2, 2);        } catch (Exception e) {            System.out.println(e.getMessage());        }        for (int i = -2; i <= 2; i++) {            for (int j = -2; j <= 2; j++) {                for (int k = -2; k <= 2; k++) {                    System.out.printf("x = %s, y = %s, z = %s: %s\n", i, j, k, table[i + 2][j + 2][k + 2]);                }            }        }    }}